#include <stdlib.h>
#include  <fcntl.h>
#include  "stdio.h"
#include  "termios.h"
#include  "unistd.h"
#include  "limits.h"
#include  <stdint.h>
#include  "time.h"
 //===================
#include <sys/select.h>
#include <sys/time.h>
 //===================


#define   UART_DEV   "/dev/ttyS3" //


void main()
{
    int fd =0;
    int RxLen=0;
    uint8_t RxBuff[1024]={0};

    //======================//
    fd = open(UART_DEV ,O_RDWR|O_NOCTTY);
    if(fd<0){
      printf("COM (%s) Open Fail ! \n",UART_DEV); //.
      return;
    }


    printf("COM (%s) Open Success ! Watting recv...\n\n",UART_DEV);
    //======================//
    struct  termios opt;          //struct termios
    tcgetattr(fd, & opt);         //

    cfsetispeed(& opt, B9600);  //(9600bps)
    cfsetospeed(& opt, B9600);  //(9600bps)

    /* c_lflag  */
    opt.c_cflag &= ~ INPCK;           //
    opt.c_cflag |= (CLOCAL |  CREAD); //CLOCAL modem ,CREAD

    /* c_lflag  */
    opt.c_lflag &= ~(ICANON | ECHO | ECHOE |  ISIG); //ICANON;ECHO;ECHOE ICANON ERASE WERASE ;ISIG INTR, QUIT, SUSP,  DSUSP 

    /* c_oflag  */
    opt.c_oflag &= ~ OPOST;             //OPOST
    opt.c_oflag &= ~(ONLCR | OCRNL);    //ONLCR-,OCRNL

    /* c_iflag  */
    opt.c_iflag &= ~(ICRNL |  INLCR);          //ICRNL ( IGNCR),INLCR NL  CR
    opt.c_iflag &= ~(IXON | IXOFF | IXANY);    //IXON XON/XOFF,IXOFF XON/XOFF,IXANY( POSIX.1XSI) 

    /* c_cflag  */
    opt.c_cflag &= ~ CSIZE;     //, CS5, CS6, CS7,  CS8,~
    opt.c_cflag |=  CS8;        //8bit
    opt.c_cflag &= ~ CSTOPB;    //CSTOPB,~
    opt.c_cflag &= ~ PARENB;    //PARENB,~

    /* c_cc[NCCS]  */
    opt.c_cc[VTIME] = 0 ;   //(10),0.1  202
    opt.c_cc[VMIN] = 0 ;    //,0,838READ
    /* new_cfg.c_cc[VMIN]   =   8;//DATA_LEN;
       new_cfg.c_cc[VTIME]  =   20;//0.1  202
       8READ82
       VTIMEreadVMIN VMIN read*/

    /*TCIFLUSH  
      TCOFLUSH  
      TCIOFLUSH */
    tcflush(fd, TCIOFLUSH);         //
    tcsetattr(fd, TCSANOW, &opt);   //,TCSANOW

    while(1)
    {
        //==========()============//
         while( ((RxLen = read(fd, RxBuff,sizeof(RxBuff))) > 0)   )
         {
	       RxBuff[RxLen] = 0; 
    	   printf( "%s",RxBuff);

         }

    }

}
